window.onload=function(){$("html").removeClass("html-hidden"),$('[ajax-selector^="navbar"] [ajax-selector="dropdown"]').each(function(){if($(this).find('[ajax-selector="dropdown-menu"]').length){var e=$(this).find('[ajax-selector="dropdown-menu"]');e.offset().left+e.width()<=$("html").width()?e.removeClass("dropdown-edge"):e.addClass("dropdown-edge")}})};